mapper.select:
SELECT `softwareName` AS `softwarename`,`APKName` AS `apkname`,`softwareSize` AS `softwaresize`, `flatformId` AS `flatformid`,`id`, (SELECT valueName FROM `data_dictionary` WHERE typeName='所属平台' AND valueId=flatformId) AS specificFlatformid, `categoryLevel1` AS `categorylevel1`, (SELECT `categoryName` FROM `app_category` WHERE categoryLevel1=id) AS specificCategorylevel1, `categoryLevel2` AS `categorylevel2`, (SELECT `categoryName` FROM `app_category` WHERE categoryLevel2=id) AS specificCategorylevel2, `categoryLevel3` AS `categorylevel3`, (SELECT `categoryName` FROM `app_category` WHERE categoryLevel3=id) AS specificCategorylevel3, `status`, (SELECT valueName FROM `data_dictionary` WHERE typeName='APP状态' AND valueId=STATUS) AS `specificStatus`, `downloads`, `versionId` AS versionid, (SELECT `versionNo` FROM `app_version` WHERE `id`=versionId) AS specificVersionid FROM `app_info`
and softwareName like "%"#{softwareName,jdbcType=VARCHAR}"%"
and status=#{status,jdbcType=BIGINT}
and flatformId=#{flatformId,jdbcType=BIGINT}
and status=#{categoryLevel3,jdbcType=BIGINT}
and categoryLevel2=#{categoryLevel2,jdbcType=BIGINT}
and categoryLevel1=#{categoryLevel1,jdbcType=BIGINT}
